def count_handle(array)
  puts "Il y'a #{array.count} journalistes dans cette array !".red
  sleep 1
end

def digit_handle(array)
  count = 0
  array.each do |n|
    if n.count("0-9") > 0
      count += 1
    end
  end
  puts "Il y'a #{count} handle qui contienne des chiffre".red
  sleep 1
end

def has_upper(array)
  count = 0
  array.each do |n|
    n.slice!(0)
    if n.slice(0).count("A-Z") > 0
      count += 1
    end
  end
  puts "Il y'a #{count} qui commence pars une Majuscles".red
  sleep 1
end

def contain_upper(array)
  count = 0;
  array.each do |n|
    if n.count("A-Z") > 0
      count += 1
    end
  end
  puts "Il y'a #{count} handle qui contienne une majuscule".red
  sleep(1)
end

def count_underscore(array)
  count = 0
  array.each do |n|
    if n.count("_") > 0
      count += n.count("_")
    end
  end
  puts "il y'a #{count} underscore dans tout les pseudo conffondu ".red
  sleep 1
end

def aude_search(array)
  count = 0
  array.each do |n|
    if n.include?("aude") || n.include?("Aude")
      count += 1
    end
  end
  puts "Il y'a #{count} handle qui contienne aude".red
  sleep 1
end

def alphabetize(array)
  array.each do |n|
    n[0] = ""
  end
  puts array.sort_by(&:downcase)
  sleep 3
end

def sort_by_len(array)
  count = 0
  array.sort_by! { |word| word.length }
  while count < 50
    puts "#{count + 1}: #{array[count]}"
    count += 1
  end
  sleep 3
end

def epenser(array)
  count = 0
  while array[count] != "@epenser"
    count += 1
  end
  puts "@epenser est en position numero #{count}".red
  sleep 5
end

def answer_question(quest, array)
  if quest == 1
    count_handle(array)
  elsif quest == 2
    digit_handle(array)
  elsif quest == 3
    aude_search(array)
  elsif quest == 4
    has_upper(array)
  elsif quest == 5
    contain_upper(array)
  elsif quest == 6
    count_underscore(array)
  elsif quest == 7
    alphabetize(array)
  elsif quest == 8
    sort_by_len(array)
  elsif quest == 9
    epenser(array)
  else
    puts "Le numero que vous avez demander n'est pas valide veuiller reeseyer".red
  end
end

